import React, { Component } from "react";
import { Link } from 'react-router-dom';
import axios from 'axios'
import ProfileService from "./ProfileService";

class Profile extends Component {
  constructor(props) {
    super(props);
    this.state = { username: '', email: ''};
    this.service = new ProfileService();
  }

  componentWillReceiveProps() {
    this.setState({
      username: '',
      email: '',
      dateOfBirth: ''
    }, function() {
      this.service.getProfile(this.props.username)
      .then((response) => {
        this.setState({
          username: response.user.username,
          email: response.user.email,
          dateOfBirth: response.user.dateOfBirth
        })
      })
    })
  }

  handleChange = (event) => {
    const {name, value} = event.target;
    this.setState({ [name]: value});
  }

  updateProfile = () => {
    this.service.updateProfile(this.state.username, this.state.email)
  }

   render() {
    return (
      <div>
        <Link to='/edit'>Edit your profile!</Link>
        {/* render={() => <Profile user={this.state.loggedInUser} */}
        <br></br>
        <Link to={"/publicprofile/" + this.state.user._id}>See your public info!</Link>

        <h1>
          Your <em>ReadMe!</em> info as a {this.state.user.role}:
        </h1>
        <p>
          Username: {this.state.user.username}
          email: {this.state.user.email}
          date of birth: {this.state.dateOfBirth}
        </p>
        <div>

        </div>


      </div>
    );
  }
}


export default Profile;

