import React, { Component } from "react";
import { Link } from 'react-router-dom';
import axios from 'axios';
import ProfileService from "./ProfileService";

class Profile extends Component {
  constructor(props) {
    super(props);
    this.state = { name: '', email: '', dateOfBirth: '', user: props.user};
    this.service = new ProfileService();
  }

  componentWillReceiveProps() {
    this.setState({
      email: '',
      dateOfBirth: ''
    }, function() {
      this.service.getProfile()
      .then((response) => {
        this.setState({
          email: response.user.email,
          dateOfBirth: response.user.dateOfBirth
        })
      })
    })
  }

  handleChange = (event) => {
    const {name, value} = event.target;
    this.setState({ [name]: value});
  }

  updateProfile = () => {
    this.service.updateProfile(this.state.email, this.state.dateOfBirth)
  }

   render() {
    return (
      <div>
        <Link to={"/publicprofile/" + this.state.user._id}>See your public info!</Link>
        <br></br>
        <h3>All your profile info</h3>
        <p>This is the info that your NFC card will show.</p><Link to='/edit'>Want to edit anything? (Click here!)</Link>
        <h4>
          Your personal info:
        </h4>
        <ol>
          <li>Name: {this.state.name}</li>
          <li>email: {this.state.user.email}</li>
          <li>Date of birth: {this.state.dateOfBirth}</li>
          <li>Address: {this.state.address}</li>
          <li>City: {this.state.city}</li>
        </ol>

        <h4>
          Your emergency contact:
        </h4>
        <ol>
          <li>Name: {this.state.contactname}</li>
          <li>is your... {this.state.relationshipcontact}</li>
          <li>Phone: {this.state.contactname} </li>
        </ol>

        <h4>
          Your medical info:
        </h4>
        <ol>
          <li>Blood group: {this.state.bloodGroup}</li>
          <li>Allergies: {this.state.allergies}</li>
          <li>Disease: {this.state.diagnosis} </li>
        </ol>

        <h4>
          Your treatment info:
        </h4>
        <ol>
          <li>I need this medication: {this.state.drug}</li>
          <li>It must be administered in this way: {this.state.administration}</li>
          <li>I need an ambulance: {this.state.ambulance} </li>
          <li>Additional info: {this.state.additionalInfo} </li>
        </ol>

      </div>
    );
  }
}


export default Profile;
