import axios from 'axios';

class ProfileService {
  constructor() {
    this.service = axios.create({
      baseURL: 'http://localhost:3001/profile',
      withCredentials: true
    });
  }
  //rutas del back donde el front solicitará la info
  getProfile = (username, password) => {
    return this.service.get('/profile', {username, password})
    .then(response => response.data)
  }

  updateProfile = (username, password) => {
    return this.service.put('/profile', {username, password})
    .then(response => response.data)
  }

  deleteProfile = () => {
    return this.service.delete('/profile',)
    .then(response => response.data)
  }
}

export default ProfileService;
